import React from 'react';
import './BrewList.css';
import BrewItem from './BrewItem';

const BrewList = ({ brews, onEdit, onDelete, onAdd }) => {
    if (!brews.length) {
        return (
            <div className="text-center my-5">
                <h2 className="mb-3" style={{ color: "#FF7043" }}>
                    No brews yet{' '}
                    <span role="img" aria-label="sad">
                        ☹️
                    </span>
                </h2>
                <button
                    className="btn"
                    style={{
                        background: "#FF7043",
                        color: "#fff",
                        borderRadius: "50px",
                        boxShadow: "0 2px 8px #FF704366",
                        fontWeight: "bold",
                    }}
                    onClick={onAdd}
                >
                    Add your first brew
                </button>
            </div>
        );
    }

    return (
        <div className="brew-list-vertical">
            {brews.map((brew) => (
                <BrewItem
                    key={brew.id}
                    brew={brew}
                    onEdit={() => onEdit(brew)}
                    onDelete={() => onDelete(brew.id)}
                />
            ))}
        </div>
    );
};

export default BrewList;
