import React, { useEffect, useState } from 'react';
import BrewItem from './BrewItem';

const BrewList = ({ brews: initialBrews, onEdit, onDelete, onAdd }) => {
    const [brews, setBrews] = useState(initialBrews);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchBrews = async () => {
            try {
                const response = await fetch('/api/brews');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setBrews(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        if (!initialBrews.length) {
            fetchBrews();
        }
    }, [initialBrews]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    if (!brews.length) {
        return (
            <div className="text-center my-5">
                <h2 className="mb-3" style={{ color: "#FF7043" }}>
                    No brews yet{' '}
                    <span role="img" aria-label="sad">
                        ☹️
                    </span>
                </h2>
                <button
                    className="btn"
                    style={{
                        background: "#FF7043",
                        color: "#fff",
                        borderRadius: "50px",
                        boxShadow: "0 2px 8px #FF704366",
                        fontWeight: "bold",
                    }}
                    onClick={onAdd}
                >
                    Add your first brew
                </button>
            </div>
        );
    }

    return (
        <div>
            <h2>Brew List</h2>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Bean Type</th>
                        <th>Method</th>
                        <th>Coffee (g)</th>
                        <th>Water (g)</th>
                        <th>Rating</th>
                        <th>Notes</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {brews.map((brew) => (
                        <BrewItem
                            key={brew._id}
                            brew={brew}
                            onEdit={() => onEdit(brew)}
                            onDelete={() => onDelete(brew._id)}
                        />
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default BrewList;
