import React, { useState, useEffect } from 'react';

const initialForm = {
    name: '',
    beanType: '',
    method: '',
    coffeeGrams: '',
    waterGrams: '',
    rating: '',
    notes: ''
};

function BrewForm({ onSubmit, initialData, onCancel }) {
    const [form, setForm] = useState(initialForm);
    const [error, setError] = useState('');

    useEffect(() => {
        if (initialData) setForm(initialData);
        else setForm(initialForm);
    }, [initialData]);

    function handleChange(e) {
        const { name, value } = e.target;
        setForm(f => ({ ...f, [name]: value }));
    }

    function handleSubmit(e) {
        e.preventDefault();
        const { name, beanType, method, coffeeGrams, waterGrams, rating } = form;
        if (!name || !beanType || !method || !coffeeGrams || !waterGrams || !rating) {
            setError('All fields except notes are required.');
            return;
        }
        setError('');
        onSubmit({ ...form, coffeeGrams: Number(form.coffeeGrams), waterGrams: Number(form.waterGrams), rating: Number(form.rating) });
        setForm(initialForm);
    }

    return (
        <form onSubmit={handleSubmit} className="row g-3 mb-4">
            <div className="col-md-4">
                <label className="form-label">Name</label>
                <input name="name" className="form-control" value={form.name} onChange={handleChange} />
            </div>
            <div className="col-md-4">
                <label className="form-label">Bean Type</label>
                <input name="beanType" className="form-control" value={form.beanType} onChange={handleChange} />
            </div>
            <div className="col-md-4">
                <label className="form-label">Method</label>
                <input name="method" className="form-control" value={form.method} onChange={handleChange} />
            </div>
            <div className="col-md-4">
                <label className="form-label">Coffee (g)</label>
                <input name="coffeeGrams" type="number" className="form-control" value={form.coffeeGrams} onChange={handleChange} />
            </div>
            <div className="col-md-4">
                <label className="form-label">Water (g)</label>
                <input name="waterGrams" type="number" className="form-control" value={form.waterGrams} onChange={handleChange} />
            </div>
            <div className="col-md-4">
                <label className="form-label">Rating</label>
                <input name="rating" type="number" min="1" max="10" className="form-control" value={form.rating} onChange={handleChange} />
            </div>
            <div className="col-12">
                <label className="form-label">Notes</label>
                <textarea name="notes" className="form-control" value={form.notes} onChange={handleChange} />
            </div>
            <div className="col-12 d-flex align-items-center gap-2">
                <button type="submit" className="btn" style={{ background: "#FF7043", color: "#fff", borderRadius: "50px", boxShadow: "0 2px 8px #FF704366" }}>
                    {initialData ? 'Update' : 'Add'} Brew
                </button>
                {initialData && (
                    <button type="button" className="btn btn-secondary" style={{ borderRadius: "50px" }} onClick={onCancel}>
                        Cancel
                    </button>
                )}
                {error && <div className="ms-3 text-danger">{error}</div>}
            </div>
        </form>
    );
}

export default BrewForm;
