import React, { useEffect, useState } from 'react';
import { getBrews, createBrew, updateBrew, deleteBrew } from './api/brews';
import BrewList from './components/BrewList';
import BrewForm from './components/BrewForm';

function App() {
    const [brews, setBrews] = useState([]);
    const [filterMethod, setFilterMethod] = useState('');
    const [editingBrew, setEditingBrew] = useState(null);

    useEffect(() => {
        document.title = `Brews: ${brews.length}`;
    }, [brews.length]);

    useEffect(() => {
        async function fetchBrews() {
            try {
                const data = await getBrews(filterMethod);
                setBrews(data);
            } catch (err) {
                alert(err.message);
            }
        }
        fetchBrews();
    }, [filterMethod]);

    async function handleCreate(brew) {
        await createBrew(brew);
        fetchBrews();
    }

    async function handleUpdate(id, brew) {
        await updateBrew(id, brew);
        setEditingBrew(null);
        fetchBrews();
    }

    async function handleDelete(id) {
        await deleteBrew(id);
        fetchBrews();
    }

    return (
        <div className="container">
            <h1>Brews: {brews.length}</h1>
            <BrewForm
                onSubmit={editingBrew ? (brew) => handleUpdate(editingBrew.id, brew) : handleCreate}
                initialData={editingBrew}
                onCancel={() => setEditingBrew(null)}
            />
            <div>
                <label>
                    Filter by Method:
                    <input
                        value={filterMethod}
                        onChange={e => setFilterMethod(e.target.value)}
                        placeholder="e.g. French Press"
                    />
                </label>
            </div>
            <BrewList
                brews={brews}
                onEdit={setEditingBrew}
                onDelete={handleDelete}
            />
        </div>
    );
}

export default App;
