import React, { useState, useEffect } from 'react';

const initialForm = {
    name: '',
    beanType: '',
    method: '',
    coffeeGrams: '',
    waterGrams: '',
    rating: '',
    notes: ''
};

function BrewForm({ onSubmit, initialData, onCancel }) {
    const [form, setForm] = useState(initialForm);
    const [error, setError] = useState('');

    useEffect(() => {
        if (initialData) setForm(initialData);
        else setForm(initialForm);
    }, [initialData]);

    function handleChange(e) {
        const { name, value } = e.target;
        setForm(f => ({ ...f, [name]: value }));
    }

    function handleSubmit(e) {
        e.preventDefault();
        // Validation: all fields except notes required
        const { name, beanType, method, coffeeGrams, waterGrams, rating } = form;
        if (!name || !beanType || !method || !coffeeGrams || !waterGrams || !rating) {
            setError('All fields except notes are required.');
            return;
        }
        setError('');
        onSubmit({ ...form, coffeeGrams: Number(form.coffeeGrams), waterGrams: Number(form.waterGrams), rating: Number(form.rating) });
        setForm(initialForm);
    }

    return (
        <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
            <input name="name" placeholder="Name" value={form.name} onChange={handleChange} />
            <input name="beanType" placeholder="Bean Type" value={form.beanType} onChange={handleChange} />
            <input name="method" placeholder="Method" value={form.method} onChange={handleChange} />
            <input name="coffeeGrams" type="number" placeholder="Coffee (g)" value={form.coffeeGrams} onChange={handleChange} />
            <input name="waterGrams" type="number" placeholder="Water (g)" value={form.waterGrams} onChange={handleChange} />
            <input name="rating" type="number" min="1" max="10" placeholder="Rating" value={form.rating} onChange={handleChange} />
            <input name="notes" placeholder="Notes" value={form.notes} onChange={handleChange} />
            <button type="submit">{initialData ? 'Update' : 'Add'} Brew</button>
            {initialData && <button type="button" onClick={onCancel}>Cancel</button>}
            {error && <div style={{ color: 'red' }}>{error}</div>}
        </form>
    );
};

export default BrewForm;
