import React from 'react';
import './BrewItem.css';

const BrewItem = ({ brew, onEdit, onDelete }) => {
    return (
        <div className="brew-card d-flex flex-column justify-content-between mb-3 shadow">
            <div>
                <div className="d-flex justify-content-between align-items-center mb-2">
                    <h3 className="mb-0" style={{ color: "#FF7043", fontFamily: "'Satoshi', sans-serif" }}>{brew.name}</h3>
                    <span className="badge bg-dark text-light">{brew.method}</span>
                </div>
                <div className="mb-1" style={{ color: "#A8E6CF", fontWeight: 600 }}>
                    {"â˜…".repeat(brew.rating)}{"â˜†".repeat(5 - brew.rating)}
                </div>
                <div className="mb-1 text-secondary" style={{ fontSize: "0.95rem" }}>
                    <strong>Bean:</strong> {brew.beanType}
                </div>
                <div className="mb-1 text-secondary" style={{ fontSize: "0.95rem" }}>
                    <strong>Coffee:</strong> {brew.coffeeGrams}g &nbsp; | &nbsp;
                    <strong>Water:</strong> {brew.waterGrams}g
                </div>
                {brew.notes && (
                    <div className="mb-2" style={{ color: "#9E9E9E", fontStyle: "italic" }}>
                        <strong>Notes:</strong> {brew.notes}
                    </div>
                )}
                <div className="mb-2" style={{ fontSize: "0.85rem", color: "#9E9E9E" }}>
                    {brew.createdAt && (
                        <>Logged: {new Date(brew.createdAt).toLocaleDateString()}</>
                    )}
                </div>
            </div>
            <div className="d-flex gap-2 mt-2">
                <button
                    className="btn"
                    style={{
                        background: "#A8E6CF",
                        color: "#212121",
                        borderRadius: "50px",
                        fontWeight: "bold",
                        boxShadow: "0 2px 8px #A8E6CF66"
                    }}
                    onClick={() => onEdit(brew)}
                >
                    ðŸ–‰ Edit
                </button>
                <button
                    className="btn"
                    style={{
                        background: "#3C2F2F",
                        color: "#FF7043",
                        borderRadius: "50px",
                        fontWeight: "bold"
                    }}
                    onClick={() => onDelete(brew.id)}
                >
                    ðŸ—‘ Delete
                </button>
            </div>
        </div>
    );
};

export default BrewItem;
